import styled from "styled-components";

export const InputContainer = styled.div`
    width: 100%;
    height: 75px;
    background-color: #ACACAC;
    display: flex;
    align-items: center;
    justify-content: flex-end;
    font-size: 24px;
    input {
        width: 100%;
        height: 75px;
        background-color: #FCFCFC;
        border: 0;
        padding: 0 15px;
        font-size: 24px;
        align: right;
        font-family:'Roboto';
        color: #737373;
        text-align: right; 
    }
`
