import styled from "styled-components";

export const ButtonContainer = styled.button`
    padding: 10px;
    border: 1px solid #CDCDCD;
    background-color: #737373;
    color: #BABABA;
    font-size:24px;
    font-family: 'Roboto';
    font-weight: 700;
    flex: 1;

    &:hover {
        opacity: 0.6;
        box-shadow: inset 5px 5px 12px black;
    }
`
