import Input from './components/Input'
import Button from './components/Button'
import { Container,Content,Row } from './styles.js';
import { useState } from 'react';

const App = () => {
  const [currentNumber, setCurrentNumber] = useState('');
  const [firstNumber, setFirstNumber] = useState('0');
  const handleOnClear = () => {
    setCurrentNumber('');
    setFirstNumber('0');
  };
  const handleAddNumber = (number) => {
    setCurrentNumber(prev => `${trataPrev(number,prev)}`)
  };
  const trataPrev = (number,prev) => {
    if (number === '=') {
      if (prev.includes('+')) {
        handleSumNumbers("+");
      }
    }
    if (!prev.includes('.')) {
      if (number === '.' && prev === ''){
        prev = '0';
      } 
    } else if (number === '.' && (!prev.includes('+'))) {
      number = '';
    }
    return prev + number;
  };
  const handleSumNumbers = (operator) => {
      let nrAtu = currentNumber.split(operator);
      const sum = Number(nrAtu[0]) + Number(nrAtu[1]);
      setFirstNumber(String(sum));
      setCurrentNumber(String(sum));
  }

  return (
    <Container>
      <Content>
        <Input value={currentNumber} />
        <Row>
          <Button label="%" onClick={() => handleAddNumber('')} />
          <Button label="*" onClick={() => handleAddNumber('')} />
          <Button label="/" onClick={() => handleAddNumber('')} />
          <Button label="C" onClick={() => handleAddNumber('')} />
        </Row>
        <Row>
          <Button label="7" onClick={() => handleAddNumber('7')} />
          <Button label="8" onClick={() => handleAddNumber('8')} />
          <Button label="9" onClick={() => handleAddNumber('9')} />
          <Button label="-" onClick={() => handleAddNumber('')} />
        </Row>
        <Row>
          <Button label="4" onClick={() => handleAddNumber('4')} />
          <Button label="5" onClick={() => handleAddNumber('5')} />
          <Button label="6" onClick={() => handleAddNumber('6')} />
          <Button label="+" onClick={() => handleAddNumber('+')} />
        </Row>
        <Row>
          <Button label="1" onClick={() => handleAddNumber('1')} />
          <Button label="2" onClick={() => handleAddNumber('2')} />
          <Button label="3" onClick={() => handleAddNumber('3')} />
          <Button label="=" onClick={() => handleAddNumber('=')} />
        </Row>
        <Row>
          <Button label="," onClick={() => handleAddNumber(',')} />
          <Button label="0" onClick={() => handleAddNumber('0')} />
          <Button label="." onClick={() => handleAddNumber('.')} />
          <Button label="C" onClick={() => handleOnClear('')} />
        </Row>
      </Content>
    </Container>
  );
}

export default App;
